mapper.select:

SELECT cmid, cmname, execution_type, ifnull(schedule, "NULL") as schedule, ifnull(cm_remarks, "NULL") as cm_remarks, arg_cnt, use_yn, cuser, cdate, uuser, udate
FROM tnsda_context_model
WHERE cmid=#{cmid}

mapper.update:

UPDATE tnsda_context_model SET use_yn="N" 
WHERE cmid=#{cmid}
AND use_yn="Y"
